import java.util.*;/** * Created by mingkunyu on 2019-08-31 * 你有一个字符串s，请你在s的所有子串中，找到出现次数最多的子串，告诉老师它的出现次数。 */public class Main_360_1 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        String str = sc.next();        char[] chars = str.toCharArray();        HashMap<Character, Integer> hashMap = new HashMap<>();        int max=0;        for (int i = 0; i < chars.length; i++) {            if (!hashMap.containsKey(chars[i])) {                hashMap.put(chars[i], 1);            } else {                int num = hashMap.get(chars[i])+1;                if (num>max){                    max=num;                }                hashMap.put(chars[i], num );            }        }        System.out.println(max);    }}