/** * Created by mingkunyu on 2019-09-01 * 反转链表 */class ListNode {    int val;    ListNode next = null;    ListNode(int val) {        this.val = val;    }}public class Solution_15 {    public ListNode ReverseList(ListNode head) {        ListNode cur = head;        ListNode pre = null;        ListNode next = null;        while (cur != null) {            next = cur.next;            cur.next = pre;            pre = cur;            cur = next;        }        return pre;    }}