/** * Created by mingkunyu on 2019-09-02 * <p> * 操作给定的二叉树，将其变换为源二叉树的镜像。 */public class Solution_16 {    public class TreeNode {        int val = 0;        TreeNode left = null;        TreeNode right = null;        public TreeNode(int val) {            this.val = val;        }    }    public void Mirror(TreeNode root) {        TreeNode t = null;        if (root != null) {            t=root.left;            root.left=root.right;            root.right=t;            if (root.left!=null){                Mirror(root.left);            }            if (root.right!=null){                Mirror(root.right);            }        }    }}