import java.util.ArrayList;import java.util.List;import java.util.Stack;/** * Created by mingkunyu on 2019-09-02 * 定义栈的数据结构，请在该类型中实现一个能够得到栈中所含最小元素的min函数（时间复杂度应为O（1））。 */public class Solution_18 {    /**     * * 这道题考察的是含有最小栈的栈结构     * * 栈数据：3 4 2 5 1     * * 最小栈：3 3 2 2 1   //存储目前最小的值     */    Stack<Integer> stack = new Stack<>();    Stack<Integer> min = new Stack<>();    public void push(int node) {        stack.push(node);        if (min.empty()) {            min.push(node);        } else {            if (node < min.peek()) {                min.push(node);            } else {                min.push(min.peek());            }        }    }    public void pop() {        stack.pop();        min.pop();    }    public int top() {        return stack.peek();    }    public int min() {        return min.peek();    }}