import java.util.Arrays;import java.util.HashMap;/** * Created by mingkunyu on 2019-09-02 * 数组中有一个数字出现的次数超过数组长度的一半，请找出这个数字。例如输入一个长度为9的数组{1,2,3,2,2,2,5,4,2}。 * 由于数字2在数组中出现了5次，超过数组长度的一半，因此输出2。如果不存在则输出0。 */public class Solution_19 {    public static int MoreThanHalfNum_Solution(int[] array) {        HashMap<Integer, Integer> hashMap = new HashMap<>();        for (int i = 0; i < array.length; i++) {            if (!hashMap.containsKey(array[i])) {                hashMap.put(array[i], 1);                if (hashMap.get(array[i]) > (array.length) / 2) {                    return array[i];                }            } else {                hashMap.put(array[i], hashMap.get(array[i]) + 1);                if (hashMap.get(array[i]) > (array.length) / 2) {                    return array[i];                }            }        }        return 0;    }    public static void main(String[] args) {        int[] arr = {1, 2, 2, 2, 2, 2, 3, 4, 4};        System.out.println(MoreThanHalfNum_Solution(arr));    }}