/** * Created by mingkunyu on 2019-08-30 */public class Solution_2 {    /**     * looks good to me -->>>lgtm     * @param str     * @return     */    public static String replaceSpace(String str) {        String[] strings=str.split("\\s+");        StringBuffer out=new StringBuffer();        for (int i = 0; i <strings.length ; i++) {            out.append(strings[i].charAt(0));        }        return out.toString();    }    public static void main(String[] args) {        System.out.println(replaceSpace("looks good to me"));    }}