import java.util.ArrayList;/** * Created by mingkunyu on 2019-08-30 * 输入一个链表，按链表从尾到头的顺序返回一个ArrayList。 */public class Solution_3 {    class  ListNode{        int val;        public  ListNode next;        public ListNode(int val) {            this.val = val;            next=null;        }    }    ArrayList<Integer> arrayList=new ArrayList<>();    public ArrayList<Integer> printListFromTailToHead(ListNode listNode) {        if (listNode!=null){            this.printListFromTailToHead(listNode.next);            arrayList.add(listNode.val);        }        return arrayList;    }}