/** * Created by mingkunyu on 2019-09-01 *  非递归斐波那契 *  1、1、2、3、5、8、13、21、34、 */public class Solution_8 {    public int Fibonacci(int n) {        int fn1=1;        int fn2=1;        int fn3=0;        if(n<=0) return 0;        if (n==1 || n==2) return 1;        for (int i = 3; i <=n ; i++) {            fn3=fn2+fn1;            fn1=fn2;            fn2=fn3;        }        return fn3;    }}