/** * Created by mingkunyu on 2019-09-01 * *  跳台阶 */public class Solution_9 {    public int JumpFloor(int target) {        int first=1;        int second=2;        int total=0;        if(target<=0) return 0;        if (target==1)return 1;        if (target==2) return 2;        for (int i = 3; i <=target ; i++) {            total=first+second;            first=second;            second=total;        }        return total;    }}