package main;import java.util.Scanner;/** * Created by mingkunyu on 2019-10-09 */public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int num = scanner.nextInt();        int max = 0;        if (num % 2 == 0) {            int mid = num / 2;            max = mid * (num - mid);        } else {            int mid = num / 2;            max = (num / 2) * (num - mid);        }        System.out.println(max);    }}