package main;import java.util.Scanner;import java.util.regex.Pattern;/** * Created by mingkunyu on 2019-10-09 */public class Main1 {    public static void main(String[] args) {        Person p1 = new Person();        p1.setName("zhangsan");        Person p2 = new Person();        p2.setName("lisi");        Scanner scanner = new Scanner(System.in);        String name = scanner.next();        String pass = scanner.next();        String phone = scanner.next();        if (name.equals(p1.getName()) || name.equals(p2.getName())) {            System.out.println("该用户名已存在");            return;        }        if (!pass.matches("^[0-9a-zA-Z]{8,16}$")) {            System.out.println("密码格式错误");            return;        }        if (!phone.matches("^1[0-9]{10}$")) {            System.out.println("请输入正确的手机号码");            return;        }        System.out.println("注册成功");    }    static class Person {        public Person() {        }        public String getName() {            return name;        }        public String getPass() {            return pass;        }        public String getPhone() {            return phone;        }        public void setName(String name) {            this.name = name;        }        public void setPass(String pass) {            this.pass = pass;        }        public void setPhone(String phone) {            this.phone = phone;        }        public String name;        public String pass;        public String phone;    }}