package s_offer;/** * Created by mingkunyu on 2019-11-13 * 输入一个链表，输出该链表中倒数第k个结点。 * <p> * 快慢指针 * 快指针先往前走k步，注意判断边界，然后快慢一起走，当快指针为none的时候，慢指针走到了倒数第k个节点 */public class FindKthToTail {    public class ListNode {        int val;        ListNode next = null;        ListNode(int val) {            this.val = val;        }    }    public ListNode FindKthToTail(ListNode head, int k) {        if (head == null || k <= 0) {            return null;        }        ListNode pre = head;        ListNode last = head;        for (int i = 1; i < k; i++) {            if (pre.next == null) {                return null;            }            pre = pre.next;        }        while (pre.next != null) {            pre = pre.next;            last = last.next;        }        return last;    }}