package s_offer;import java.util.Stack;/** * Created by mingkunyu on 2019-09-26 * 题目描述 * 用两个栈来实现一个队列，完成队列的Push和Pop操作。 * 队列中的元素为int类型。 */public class StackQueue_5 {    Stack<Integer> stack1 = new Stack<Integer>();    Stack<Integer> stack2 = new Stack<Integer>();    public void push(int node) {        while (!stack2.empty()){            stack1.add(stack2.pop());        }        stack1.add(node);    }    public int pop() {        while (!stack1.empty()){            stack2.add(stack1.pop());        }        return stack2.pop();    }}