package s_offer;/** * Created by mingkunyu on 2019/12/17 */public class StrToInt {    public static int StrToInt(String str) {        if (str == null || str.length() <= 0) {            return 0;        }        boolean isN = str.charAt(0) == '-';        int ret = 0;        for (int i = 0; i < str.length(); i++) {            char c = str.charAt(i);            if (i == 0 && (c == '-' || c == '+')) {                continue;            }            if (c < '0' || c > '9') {                return 0;            }            ret = ret * 10 + c - '0';        }        return isN ? -ret : ret;    }    public static void main(String[] args) {        System.out.println(StrToInt("+123"));    }}