package s_offer;/** * Created by mingkunyu on 2019-11-15 * * 输入一个整数数组，判断该数组是不是某二叉搜索树的后序遍历的结果。 * * 如果是则输出Yes,否则输出No。假设输入的数组的任意两个数字都互不相同。 * * *  *      10 *  *    /  \ *  *   5   11 *  *  /\    \ *  * 4 9    12 *    ｜ *    3 *  左右根： [3, 4, 9, 5, 12, 11, 10] * */public class VerifySquenceOfBST {    public boolean VerifySquenceOfBST(int []sequence){        if(sequence.length==0) return false;        return IsTreeBST(sequence,0,sequence.length-1);    }    public  boolean IsTreeBST(int[] sequence, int start, int end) {        if(end<=start)  return true;        int i=start;        for(;i<end;i++){            if(sequence[i]>sequence[end]) break;        }        for(int j=i;j<end;j++){            if(sequence[j]<sequence[end]) return false;        }        return IsTreeBST(sequence,start,i-1)                && IsTreeBST(sequence,i,end-1);    }}