package s_offer;import java.util.ArrayList;/** * Created by mingkunyu on 2019-09-26 * 从尾到头打印链表 * 题目描述 * 输入一个链表，按链表从尾到头的顺序返回一个ArrayList。 */public class printListFromTailToHead_3 {    public class ListNode {        int val;        ListNode next = null;        ListNode(int val) {            this.val = val;        }    }    ArrayList<Integer> arrayList = new ArrayList<>();    public ArrayList<Integer> printListFromTailToHead(ListNode listNode) {        if (listNode != null) {            printListFromTailToHead(listNode.next);            arrayList.add(listNode.val);        }        return arrayList;    }}